import React, { useState, useEffect, useRef } from "react";
import Slider from '@mui/material/Slider';


function App() {

  const [numbersToSort, setNumbersToSort] = useState([])
  const [lengthOfArray, setLengthOfArray] = useState(40)

  //TEST VARIABLES
  const [index1, setIndex1] = useState(0)
  const [index2, setIndex2] = useState(1)
  const [colour1, setColour1] = useState(0)
  const [colour2, setColour2] = useState(1)

  //When the length of Array is changed by the slider, a new array will be generated with the new length 
  useEffect(() => {
    generateArray(lengthOfArray)
  }, [lengthOfArray])

  //Generates an array with random integers
  const generateArray = (length) => {
    let tempArray = []
    for (let i = 0; i < length; i++) {
      var randomInteger = Math.round(20 + (Math.random() * (200 - 20)))
      tempArray.push(randomInteger)
    }
    setNumbersToSort(tempArray)
  }

  //Conducts a bubble sort on an array
  const bubbleSort = (arr) => {

    for (var i = 0; i < arr.length; i++) {

      // Last i elements are already in place  
      for (var j = 0; j < (arr.length - i - 1); j++) {

        // Checking if the item at present iteration 
        // is greater than the next iteration

        if (arr[j] > arr[j + 1]) {
          setIndex1(j)
          setIndex2(j+1)
          // If the condition is true then swap them
          var temp = arr[j]
          arr[j] = arr[j + 1]
          arr[j + 1] = temp
        }
      }
    }

    setNumbersToSort([...arr])
  }

  //Conducts a Quick sort on an array



  //Handles changing the value of the slider
  const handleChange = (event, newValue) => {
    setLengthOfArray(newValue);
  };

  return (
    <div className="App">
      {/* <button onClick={() => { console.log(numbersToSort) }}>See Array</button> */}
      {/* <button onClick={() => { generateArray(lengthOfArray) }}>Generate Array</button> */}
      <button onClick={() => { bubbleSort(numbersToSort) }}>Bubble Sort</button>

      <h1>{lengthOfArray}</h1>
      <Slider defaultValue={40} aria-label="Default" valueLabelDisplay="auto" onChange={handleChange} min={20} max={200} />
      <ul>
        {numbersToSort.map((number, index) => (
          <li key={index} style="color: blue">{number}</li>
        ))}
      </ul>


    </div>
  );
}

export default App;
